import React, { Component } from 'react';
import {getMovies} from '../services/fakeMovieService';
import {getGenres} from '../services/fakeGenreService'
import Pagination from "./common/pagination";
import ListGroup from './common/genre';
import Like from './common/like';
import Paginate  from '../utils/paginate'


export default class Movies extends Component {
    state={
        movies: [],
        genre:[],
        currentPage: 1,
        pageSize: 4
    }; 
    handleDelete = movie =>{
        const movies=this.state.movies.filter(m => m._id !== movie._id);
        this.setState({movies})
    };
    handleLike=movie=>{
        const movies=[...this.state.movies]
        const index=movies.indexOf(movie);
        movies[index]={...movies[index]};
        movies[index].liked=!movies[index].liked;
        this.setState({movies});;
    };
    handleChange=page=>{
       this.setState({currentPage:page});
    };

    componentDidMount(){
        const genre =[{name:'All Movies'}]
        this.setState({movies:getMovies(),genre:getGenres()});
    };
    handleGenreSelect=genre=>{ 
        this.setState({selectedGenre:genre })

    };
    render() { 
        const {length: count}=this.state.movies;
        const {pageSize,currentPage,selectedGenre,movies:allMovies}=this.state;
        if (count === 0)
          return <p>There are no movie in database</p>
          const filtered =selectedGenre ? allMovies.filter (m=> m.genre._id === selectedGenre._id) :allMovies;
          const movies =Paginate(filtered,currentPage,pageSize)
        return (

                <div className="row">
                    <div className="col-2">
                        <ListGroup 
                        items={this.state.genre}
                        selectItems={this.state.selectedGenre}
                        onItemSelect={this.handleGenreSelect}
                        />
                    </div>
                    <div className="col">
                    <p> There are {filtered.length} movie in database</p>
                        <table className="table">
                            <thead className="table-dark">
                                <tr>
                                <th scope="col">Title</th>
                                <th scope="col">Genere</th>
                                <th scope="col">Stock</th>
                                <th scope="col">Rate</th>
                                <th scope="col">Likes</th>
                                <th scope="col">Delete</th>
                                </tr>
                            </thead>
                            <tbody>
                                {movies.map(movie =>(
                                    <tr key={movie._id}>
                                        <td>{movie.title}</td>
                                        <td>{movie.genre.name}</td>
                                        <td>{movie.numberInStock}</td>
                                        <td>{movie.dailyRentalRate}</td>
                                        <td><Like liked={movie.liked} onClick={()=>this.handleLike(movie)}/></td>
                                        <td><button onClick={() => this.handleDelete(movie)} className="btn btn-danger">Delete</button></td>
                                    </tr> 
                                ))}
                            </tbody>
                        </table>
                        <Pagination 
                        itemsCount={filtered.length} 
                        pageSize={pageSize}
                        currentPage={currentPage}
                        onPageChange={this.handleChange}
                        /> 
                    </div>
                </div>




        );
    }
}

