import React, { Component } from 'react';
import {getMovies} from '../services/fakeMovieService';
import Pagination from "./common/pagination";
import Like from './common/like';
import Paginate  from '../utils/paginate'

export default class Movies extends Component {
    state={
        movies: getMovies(),
        currentPage: 1,
        pageSize: 5
    }; 
    handleDelete = movie =>{
        const movies=this.state.movies.filter(m => m._id !== movie._id);
        this.setState({movies})
        // console.log(movie);
    };
    handleLike=movie=>{
        const movies=[...this.state.movies]
        const index=movies.indexOf(movie);
        movies[index]={...movies[index]};
        movies[index].liked=!movies[index].liked;
        this.setState({movies});
        // console.log(movie);
    };
    handleChange=page=>{
       this.setState({currentPage:page});
    };

    render() { 
        if (this.state.movies.length === 0)
          return <p>There are no movie in database</p>
          const movies =Paginate(this.s)
        return (
            <React.Fragment>
             <p> There are {this.state.movies.length} movie in database</p>
          <table className="table table-striped table-hover">
            <thead>
                <tr>
                <th scope="col">Title</th>
                <th scope="col">Genere</th>
                <th scope="col">Stock</th>
                <th scope="col">Rate</th>
                </tr>
            </thead>
            <tbody>
                {this.state.movies.map(movie =>(
                    <tr key={movie._id}>
                        <td>{movie.title}</td>
                        <td>{movie.genre.name}</td>
                        <td>{movie.numberInStock}</td>
                        <td>{movie.dailyRentalRate}</td>
                        <td><Like liked={movie.liked} onClick={()=>this.handleLike(movie)}/></td>
                        <td><button onClick={() => this.handleDelete(movie)} className="btn btn-danger">Delete</button></td>
                    </tr> 
                 ))}
            </tbody>
          </table>
          <Pagination 
          itemsCount={this.state.movies.length} 
          pageSize={this.state.pageSize}
          currentPage={this.state.currentPage}
          onPageChange={this.handleChange}
          /> 
            </React.Fragment>


        );
    }
}

